import React from 'react';
import { storiesOf } from '@storybook/react';
import { action } from '@storybook/addon-actions';
import Typography from '@material-ui/core/Typography';
import { object, boolean } from '@storybook/addon-knobs';

import Table from './index';
import {
  FileCell,
  TableRow,
  TableCell,
  IconsCell,
  LoadingCell,
} from './components';

const categoryName = 'ElementalComponents/Table';

const renderHead = ({ head = [] }) => (
  <TableRow>
    {head.map((label) => (
      <TableCell key={label}>
        <Typography variant="body2">
          {label}
        </Typography>
      </TableCell>
    ))}
  </TableRow>
);

const renderRow = ({ row }) => (
  <TableRow
    key={row.id}
    hover
  >
    <FileCell ext={row.ext} src={row.src}>
      <Typography variant="body1">
        {row.name}
      </Typography>
    </FileCell>
    <TableCell>
      <Typography variant="body1" color="secondary">
        Only you
      </Typography>
    </TableCell>
    <TableCell>
      <Typography variant="body1" color="secondary">
        {row.lastModified}
      </Typography>
    </TableCell>
    <TableCell align="right">
      <Typography variant="body2">
        <IconsCell
          storageLimitWarning={false}
          upgradeOnClick={(e) => { e.preventDefault(); }}
          i18n={{
            warning: "Backup Limit Reached",
            description: "This file is not backed up, upgrade to Space Pro to back up this file.",
            button: "Upgrade to Space Pro",
          }}
        />
      </Typography>
    </TableCell>
  </TableRow>
);

const renderLoadingRows = () => [...Array(20)].map((_, index) => (
  <TableRow
    key={index}
  >
    <TableCell>
      <LoadingCell isIconCell />
    </TableCell>
    <TableCell>
      <LoadingCell />
    </TableCell>
    <TableCell>
      <LoadingCell />
    </TableCell>
    <TableCell>
      <LoadingCell isLastCell/>
    </TableCell>
  </TableRow>
));

